package com.inhertance.example;

public class BitWiseOper {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int i=8;          //    1 0 0 0
     int j=10;			//   1 0 1 0
     					//   1 0 0 0   bitwise or
     System.out.println(i & j); //8
     System.out.println(i | j); //10
	}

}
